import React from 'react';
import PopupWithForm from '../PopupWithForm/PopupWithForm.js';

function EditAvatarPopup(props) {

  const userAvatar = React.useRef();
   

  function handleSubmit(evt) {
    evt.preventDefault();
  
    onUpdateAvatar({
      avatar: userAvatar.current.value
    });
  } 

  return (
    <PopupWithForm 
      name='change-avatar' 
      title='Обновить аватар'
      submitButton='Сохранить'
      isOpen={isEditAvatarPopupOpen}
      onClose={closeAllPopups}
    >
      <input id="link-avatar" name="linkAvatar" className="popup__input popup__input_type_link-avatar" type="url" placeholder="Ссылка на аватар" ref={userAvatar} required />
      <span id="link-avatar-error" className="popup__error" />
    </PopupWithForm>
  )
}

export default EditAvatarPopup;
