import React from 'react';

function PopupWithForm(props) {
 let popupOpenClass;

 props.isOpen ? popupOpenClass='popup__opened' : popupOpenClass='';

//   if (props.isOpen) {
//     popupOpenClass = 'popup__opened';
//  }
//   else {
//     popupOpenClass = '';
//  }

  return (
    <div className={`popup popup_type_${props.name} ${popupOpenClass}`}>
        <div className="popup__content">
          <button type="button" className="popup__close" />
          <form name={props.name} className="popup__form popup__form_profile">
            <h3 className="popup__form-title">{props.title}</h3>
            {props.children}
            <button type="submit" className="popup__form-save">{props.submitButton}</button>
          </form>  
        </div>
      </div>
  )
}


export default PopupWithForm;
